const comps = $input.item.json.body.components || [];

const getComp = (type, fallback) => {
  const c = comps.find(c => c.type === type);
  return c && c.name && c.name !== 'Unknown' ? c.name : fallback;
};

// Brand new strictly-enforced prompt for Nano Banana Pro
let prompt = "Masterpiece 8k photography, hyper-realistic gaming room setup, Unreal Engine 5 render style. ";

// Enforce Case and Internals
prompt += "The centerpiece is a high-end PC inside a [" + getComp('CASE', 'premium glass ATX case') + "]. ";
prompt += "Clearly visible inside the PC is a [" + getComp('VGA', 'massive RTX graphics card') + "] and a glowing [" + getComp('COOLING', 'AIO liquid cooler') + "]. " ;

// Enforce Peripherals on Desk
prompt += "Sitting on the sleek black wooden desk is a clear, large [" + getComp('MONITOR', 'curved gaming monitor') + "]. ";
prompt += "In front of the monitor is a [" + getComp('KEYBOARD', 'RGB mechanical keyboard') + "] and a [" + getComp('MOUSE', 'wireless gaming mouse') + "]. ";
prompt += "Resting on a stand nearby is a [" + getComp('HEADSET', 'premium gaming headset') + "]. ";

prompt += "All components must be clearly visible and accurately represented. Cinematic studio lighting, dark neon aesthetic, breathtaking raytracing.";

// Re-add image to image logic if available
const validImages = comps.map(c => c.image).filter(img => img && typeof img === 'string' && img.startsWith('http'));

const inputObj = {
  prompt: prompt,
  aspect_ratio: '16:9',
  output_format: 'png',
  seed: Math.floor(Math.random() * 2147483647)
};

if (validImages.length > 0) inputObj.image = validImages[0];

$input.item.json.replicatePayloadJSON = JSON.stringify({ input: inputObj });
return $input.item;

let out = $input.item.json.output;
let finalUrl = null;

if (out) {
  if (Array.isArray(out) && out.length > 0) {
    finalUrl = out[0];
  } else if (typeof out === 'string') {
    finalUrl = out;
  } else {
    try { finalUrl = JSON.stringify(out); } catch(e) {}
  }
}

$input.item.json.finalResult = { imageUrl: finalUrl };
return $input.item;
