const comps = $input.item.json.body.components || [];

const getComp = (type, fallback) => {
  const c = comps.find(c => c.type === type);
  return c && c.name && c.name !== 'Unknown' ? c.name : fallback;
};

let prompt = "A hyper-realistic 8k photography of an ultimate complete gaming PC setup room corner. ";
prompt += "On the sleek wooden desk, there is a " + getComp('MONITOR', 'gaming monitor') + ", ";
prompt += "a " + getComp('KEYBOARD', 'mechanical keyboard') + ", ";
prompt += "a " + getComp('MOUSE', 'gaming mouse') + ", ";
prompt += "and a " + getComp('HEADSET', 'professional gaming headset') + " resting on a stand. ";
prompt += "The main PC case is a " + getComp('CASE', 'premium glass ATX case') + ", glowing with stunning RGB lighting. ";
prompt += "Clearly visible inside the case is a " + getComp('VGA', 'high-end graphics card') + " mounted on a " + getComp('MB', 'gaming motherboard') + ", ";
prompt += "cooled by " + getComp('COOLING', 'premium liquid cooling') + " and " + getComp('FANS', 'RGB case fans') + ", ";
prompt += "with " + getComp('MEMORY', 'RGB RAM') + ". ";
prompt += "Dramatic studio lighting, cinematic atmosphere, hyper-detailed textures, breathtaking reflection raytracing.";

const validImages = comps.map(c => c.image).filter(img => img && typeof img === 'string' && img.startsWith('http'));

const inputObj = {
  prompt: prompt
};
if (validImages.length > 0) inputObj.image = validImages[0];
if (validImages.length > 1) inputObj.image_2 = validImages[1];
if (validImages.length > 2) inputObj.image_3 = validImages[2];
if (validImages.length > 3) inputObj.image_4 = validImages[3];

$input.item.json.replicatePayloadJSON = JSON.stringify({ input: inputObj });

return $input.item;
